mapper.select:
SELECT B.BOARD_NO, B.BOARD_TITLE, B.BOARD_HIT, B.BOARD_DT, B.MNM FROM ( SELECT B.BOARD_NO, B.BOARD_TITLE, B.BOARD_HIT, TO_CHAR(B.BOARD_DT, 'YYYY-MM-DD') AS BOARD_DT, M.MNM, ROW_NUMBER() OVER(ORDER BY B.BOARD_NO DESC) AS RNUM FROM BOARD B INNER JOIN MEMBERS M ON B.MNO = M.MNO WHERE B.BOARD_DEL = 0
AND B.BOARD_TITLE LIKE '%' || #{searchTxt} || '%'
AND M.MNM LIKE '%' || #{searchTxt} || '%'
) B WHERE B.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE BOARD SET BOARD_TITLE = #{boardTitle},
                 BOARD_CON = #{boardCon}
WHERE BOARD_NO = #{boardNo}

UPDATE OBOARD SET OB_CON = #{obCon}
WHERE OB_NO = #{obNo}
AND MNO = #{mno}

UPDATE OBOARD SET OB_DEL = 1
WHERE OB_NO = #{obNo}
AND MNO = #{mno}
