mapper.select:
SELECT M.MNO, M.MID, M.MNM, JOINDT FROM (SELECT MNO, MID, MNM, TO_CHAR(JOINDT, 'YYYY-MM-DD') AS JOINDT, ROW_NUMBER() OVER(ORDER BY MNO DESC) AS RNUM FROM MEMBERS WHERE 1 = 1
AND MID LIKE '%' || #{searchTxt} || '%'
AND MNM LIKE '%' || #{searchTxt} || '%'
) M WHERE M.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE MEMBERS SET MNM = #{mnm},
                   
                   MPW = #{mpw},
                   
                   MBIRTH = #{mbirth},
                   MHOBBY = #{mhobby}
WHERE MNO = #{mno}

mapper.select:
		SELECT MNO, MNM, CASE WHEN MPW = #{mpw}
                     		  THEN 'TRUE'
                      		  ELSE 'FALSE'
               				  END AS MPW_RES
		FROM MEMBERS
		WHERE MID = #{mid}
